"use strict";
let number1 = 10;
let number2 = 20;
console.log(`${number1} é maior que ${number2}: ${number1 > number2}`);
let menorQue = number1 < number2;
console.log(`${number1} é menor que ${number2}: ${menorQue}`);
let maiorOuIgualQue = number1 >= number2;
console.log(`${number1} é maior ou igual que ${number2}: ${maiorOuIgualQue}`);
let menorOuIgualQue = number1 <= number2;
console.log(`${number1} é menor ou igual que ${number2}: ${menorOuIgualQue}`);
let igualQue = number1 == number2;
console.log(`${number1} é igual a ${number2}: ${igualQue}`);
let diferenteQue = number1 != number2;
console.log(`${number1} é diferente de ${number2}: ${diferenteQue}`);
let name1 = "Cleyson";
let name2 = "Elton";
console.log("A string " + name1 + " é igual a string " + name2);
console.log(name1 == name2);
